import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

export default class App extends Component {
  state = {
    persons: [
      { id: '1', name: 'Max', age: '28' },
      { id: '2', name: 'Manu', age: '29' },
      { id: '3', name: 'Stephanie', age: '26' }
    ],
    showPersons: false
  }
  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: newName, age: '21' },
        { name: 'Manu', age: '29' },
        { name: 'Stephanie', age: '24' }
      ]
    });
  }

  nameChangedHandler = (event, id) => {
    const person = this.state.persons.findIndex(id);
    this.setState({
      persons: [
        { name: 'Max', age: '21' },
        { name: event.target.value, age: '29' },
        { name: 'Stephanie', age: '24' }
      ]
    });
  }

  togglePersonHandler = () => {
    this.setState({
      showPersons: !this.state.showPersons
    });
  }

  deletePersonHandler = (personIndex) => {
      const persons = [...this.state.persons];
    persons.splice(personIndex, 1);
    this.setState({
      persons: persons
    })
  }

  render(){
    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    }

    let persons = null;

    if( this.state.showPersons ){

      persons = (

      <div>
        {
          this.state.persons.map((person, index) => {
            return <Person 
            click={() => this.deletePersonHandler(index)}
            name = {person.name} 
            age={person.age}
            key={person.id}
            changed={ (event) => this.nameChangedHandler(event, person.id)} />
          })}
      {/* <Person 
      name={this.state.persons[0].name} 
      age={this.state.persons[0].age} />
      <Person 
      name={this.state.persons[1].name} 
      age={this.state.persons[1].age} 
      click={this.switchNameHandler.bind(this, 'Max!')} 
      changed={this.nameChangedHandler}>My Hobbies: Racing</Person>
      <Person 
      name={this.state.persons[2].name} 
      age={this.state.persons[2].age}/> */}
    </div>
     ); 

    }

    return (
      <div className="App">
         <header className="App-header">
         <a className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        {/* <button style={style} onClick={() => this.switchNameHandler('Maxasd')}>Switch Name</button> */}
        <button style={style} onClick={this.togglePersonHandler}>Switch Name</button>
        { persons}
        {/* { 
          this.state.showPersons === true ? 
            <div>
            <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
            <Person name={this.state.persons[1].name} age={this.state.persons[1].age} click={this.switchNameHandler.bind(this, 'Max!')} changed={this.nameChangedHandler}>My Hobbies: Racing</Person>
            <Person name={this.state.persons[2].name} age={this.state.persons[2].age}/>
            </div> : null

        } */}

       </div>

     );
  }

}
